  DELETE_ARTICLE,
  GET_ARTICLES,
  GET_CHAPTERS,
  ADD_CHAPTER
} from '../../queries';

const ERROR_TEXT = 'Sorry, deleting your article did not work...';
const SUCCESS_TEXT = 'Great, your article is now removed!';

class RemoveArticle extends Component {
  state = {
    openSuccessSnackBar: false,
    openErrorSnackBar: false,
    openDialog: false,
    deleteArticle: {}
  };

  handleDelete = () => {
    this.state
      .deleteArticle()
      .then(() => {
        this.setState({
          openSuccessSnackBar: true,
          openDialog: false
        });
        setTimeout(() => {
          this.setState({
            openSuccessSnackBar: false
          });
        }, 3000);
      })
      .catch(() => {
        this.setState({
          openErrorSnackBar: true,
          openDialog: false
        });
        setTimeout(() => {
          this.setState({
            openErrorSnackBar: false
          });
        }, 3000);
      });
  };

  handleClose = deleteArticle => {
    this.setState({ openDialog: false });
  };

  handleDialog = deleteArticle => {
    this.setState({
      openDialog: true,
      deleteArticle: deleteArticle
    });
  };

  render() {
    const { fullScreen } = this.props;
    return (
      <Query query={GET_CHAPTERS}>
        {({ loading, error, data }) => {
          if (loading) return <p>Loading...</p>;
          if (error) return <p>Error :(</p>;
          console.log(data);

          return (
            <div className="HandleArticle">
              {data.getChapters
                .sort((a, b) => a.number > b.number)
                .map(({ _id, number }) => {
                  return (
                    <div className="HandleArticle__Chapter" key={number}>
                      Chapite {number}
                    </div>
                  );
                })}
              <Mutation
                mutation={ADD_CHAPTER}
                update={(cache, { data }) => {
                  const chapters = cache.readQuery({
                    query: GET_CHAPTERS
                  }).getChapters;
                  cache.writeQuery({
                    query: GET_CHAPTERS,
                    data: { getChapters: chapters.concat([data.addChapter]) }
                  });
                }}
              >
                {(addChapter, { loading, error }) => (
                  <div>
                    <form
                      className="HandleArticle__AddChapter"
                      onSubmit={e => {
                        e.preventDefault();
                        addChapter({
                          variables: { number: data.getChapters.length + 1 }
                        });
                      }}
                    >
                      <Button
                        className="HandleArticle__AddChapter__Button"
                        variant="outlined"
                        type="submit"
                      >
                        {loading ? 'Loading...' : 'Add a chapter'}
                      </Button>
                    </form>
                    {error && <p>Error :( Please try again</p>}
                  </div>
                )}
              </Mutation>
            </div>
          );
        }}
