import React, { Component } from "react";
import { Query } from "react-apollo";
import Article from "../Article/Article";
import { GET_ARTICLES } from "../../queries";

class Home extends Component {
  constructor(props) {
    super(props);
    this.romanNumResultArr = [];
    this.RomanNumerals = [
      ["I", "IV", "V", "IX"],
      ["X", "XL", "L", "XC"],
      ["C", "CD", "D", "CM"],
      ["M"]
    ];
  }

  convertToRomanUnit = (num, romArr) => {
    var arrayToBeUsed = romArr;
    if (num <= 3) {
      while (num > 0) {
        this.romanNumResultArr.push(arrayToBeUsed[0]);
        num--;
      }
    }
    if (num === 4) {
      this.romanNumResultArr.push(arrayToBeUsed[1]);
    }

    if (num >= 5 && num < 9) {
      this.romanNumResultArr.push(arrayToBeUsed[2]);
      while (num - 5 > 0) {
        this.romanNumResultArr.push(arrayToBeUsed[0]);
        num--;
      }
    }

    if (num === 9) {
      this.romanNumResultArr.push(arrayToBeUsed[1]);
    }
  };

  convertToRoman = num => {
    //Roman numbers can't exceed 3999
    if (num >= 4000) {
      return "Unfortunately, numbers from 4000 and above do not have roman numerals";
    }

    //Break the number into an array of numbers
    var givenNumeral = num
      .toString()
      .split("")
      .map(Number);

    //The length of the givenNumeral tells us the value of the number - 2 would mean ten and 3, hundred.
    for (let i = givenNumeral.length; i > 0; i--) {
      this.convertToRomanUnit(givenNumeral.shift(), this.RomanNumerals[i - 1]);
    }

    var result = this.romanNumResultArr.join("");

    console.log(result);
    return result;
  };
  render() {
    return (
      <div className="Home">
        <div className="Home__Presentation">
          <h1 className="Home__Presentation__Title">MOONBOARD</h1>
        </div>
        <Query query={GET_ARTICLES}>
          {({ data, loading, error }) => {
            if (loading) return <div>LOADING...</div>;
            if (error) return <div>Error</div>;
            return (
              <div className="Home__Body">
                {!data.getArticles.length && (
                  <p>
                    <strong>You have not added any articles yet</strong>
                  </p>
                )}
                {data.getArticles.map((articleByChapter, index) => (
                  <div className="Home__Body__Chapter">
                    <h1>CHAPTER {this.convertToRoman(index + 1)}</h1>
                    {articleByChapter.map(article => (
                      <Article key={article._id} article={article} />
                    ))}
                  </div>
                ))}
              </div>
            );
          }}
        </Query>
      </div>
    );
  }
}

export default Home;
